#include <iostream>
#include <vector>
#include <numeric>

using namespace std;

int main() {
    // Donuts: 36 donuts, cost $68 per 12 donuts
    int donuts = 36;
    double donut_cost_per_12 = 68.0;
    double donut_cost = (donuts / 12) * donut_cost_per_12;

    // Mini cupcakes: 24 mini cupcakes, cost $80 per 12 mini cupcakes
    int mini_cupcakes = 24;
    double mini_cupcake_cost_per_12 = 80.0;
    double mini_cupcake_cost = (mini_cupcakes / 12) * mini_cupcake_cost_per_12;

    // Mini cheesecakes: 72 mini cheesecakes, cost $55 per 12 mini cheesecakes
    int mini_cheesecakes = 72;
    double mini_cheesecake_cost_per_12 = 55.0;
    double mini_cheesecake_cost = (mini_cheesecakes / 12) * mini_cheesecake_cost_per_12;

    // Total cost
    double total_cost = donut_cost + mini_cupcake_cost + mini_cheesecake_cost;

    cout << "Total cost: $" << total_cost << endl;

    return 0;
}
